mapper.select:
select pd_orderstate, to_char(p_buydate, 'YYYY-MM-DD') p_buydate, c_num,
 refund_history_view1.p_num, rf_num, b_num, b_name, to_char(rf_confirmdate, 'YYYY-MM-DD') rf_confirmdate,
  rf_price
from refund_history_view1
full outer join refund_history_view2
on refund_history_view1.p_num = refund_history_view2.p_num




		(to_char(p_buydate, 'YYYY-MM-DD') between #{date_start} and #{date_end}) 


			AND (rf_num LIKE '%'||#{keyword}||'%')


				AND pd_orderstate = #{pd_orderstate}


				AND pd_orderstate = #{pd_orderstate}


				AND pd_orderstate = #{pd_orderstate}



			AND (b_name LIKE '%'||#{keyword}||'%')


				AND pd_orderstate = #{pd_orderstate}


				AND pd_orderstate = #{pd_orderstate}


				AND pd_orderstate = #{pd_orderstate}







	order by p_buydate


	order by p_buydate desc
